import{_ as e}from"./plugin-vue_export-helper-c27b6911.js";import{o as r,c as t,e as o}from"./app-f9e02d8f.js";const a={},s=o('<h1 id="程序员厨师指南" tabindex="-1"><a class="header-anchor" href="#程序员厨师指南" aria-hidden="true">#</a> 程序员厨师指南</h1><h2 id="冻水饺" tabindex="-1"><a class="header-anchor" href="#冻水饺" aria-hidden="true">#</a> 冻水饺</h2><p><b style="color:#3e547d;"><strong>食材：</strong></b>冻水饺若干(无需解冻)、水、 食盐少许</p><p><b style="color:#3e547d;"><strong>步骤：</strong></b>在锅中放入能浸没所有水饺的水量，开大火直至锅底出现密集小水泡时放入所有水饺，搅和搅和防止水饺粘锅，然后放入少量食盐(加入食盐不容易煮烂还可以加速饺子的溶解速度)，直到锅中水沸腾时放入一点凉水使其不沸腾，如此重复三次，等到水饺全部浮上来即可出锅。</p><h2 id="可乐鸡翅" tabindex="-1"><a class="header-anchor" href="#可乐鸡翅" aria-hidden="true">#</a> 可乐鸡翅</h2><p><b style="color:#3e547d;"><strong>食材：</strong></b>超市买的冻鸡翅、水、一小块去皮生姜、一小把葱白、料酒、生抽、蚝油、食盐</p><p><b style="color:#3e547d;"><strong>步骤：</strong></b>把鸡翅放入水中清洗，清洗完成后在鸡翅的背部划上几刀，正反面都划(方便入味)。一小块去皮生姜切好放入碗中备用。一小把葱白切成小段和生姜放一起。凉水和鸡翅一起下锅，放入料酒，煮到水滚烫，然后捞出。然后加入食用油，油热以后把鸡翅一个一个夹进锅中。一面煎好之后煎下一面。煎到焦黄即可。然后加入500ml可乐，再加入姜片和葱白，再加入适量的生抽和少许蚝油。再加入适量的食盐以及少许料酒。把汤汁烧至浓稠即可出锅。</p><h2 id="蛋炒饭" tabindex="-1"><a class="header-anchor" href="#蛋炒饭" aria-hidden="true">#</a> 蛋炒饭</h2><p><b style="color:#3e547d;"><strong>食材：</strong></b>饭、鸡蛋、猪油(或者食用油)、鸡精，生抽</p><p><b style="color:#3e547d;"><strong>步骤：</strong></b>先在锅里放油，五成油温的时候放入鸡蛋，鸡蛋炒散定型之后加入饭，把饭压散，力量不要太大。压散之后放入鸡精。翻几下后放入生抽</p>',10),n=[s];function d(c,h){return r(),t("div",null,n)}const _=e(a,[["render",d],["__file","程序员厨师指南.html.vue"]]);export{_ as default};
